import base64
import os

# Create directory if it doesn't exist
os.makedirs('public/images/tech', exist_ok=True)

# Base64 data for each logo
logos = {
    'groq.png': '''data:image/png;base64,''',
    'llamaindex.jpeg': '''data:image/jpeg;base64,\n...(truncated for brevity)''',
}

# Save each logo
for filename, base64_data in logos.items():
    # Remove the data URL prefix if present
    if ',' in base64_data:
        base64_data = base64_data.split(',')[1]
    
    # Decode base64
    image_data = base64.b64decode(base64_data)
    
    # Write to file
    filepath = f'public/images/tech/{filename}'
    with open(filepath, 'wb') as f:
        f.write(image_data)
    print(f'Saved {filepath}')

print('All logos saved successfully!')
